import React from 'react';

class TodoForms extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            responsible: '',
            description: '',
            priority: 'low'
        };
    }

    handleInput(e){
        console.log(e.target.value, e.target.name)
    }

    render() {
        return (
            <div className="card">
                <form className="card-body">
                    <div className="form-group">
                        <input 
                            type="text"
                            name="title"
                            onChange={this.handleInput}
                            className="form-control"
                            placeholder="Title"
                        />
                    </div>
                    <div className="form-group">
                        <input 
                            type="text"
                            name="responsible"
                            onChange={this.handleInput}
                            className="form-control"
                            placeholder="Responsible"
                        />
                    </div>
                    <div className="form-group">
                        <input 
                            type="text"
                            name="description"
                            onChange={this.handleInput}
                            className="form-control"
                            placeholder="Description"
                        />
                    </div>
                    <div className="form-group">
                        <select 
                            name="priority" 
                            className="form-control">
                            <option>Low</option>
                            <option>Medium</option>
                            <option>High</option>
                        </select>
                        <input 
                            type="text"
                            name="priority"
                            onChange={this.handleInput}
                            className="form-control"
                            placeholder="Priority"
                        />
                    </div>
                </form>
            </div>
        );
    }
}

TodoForms.propTypes = {};

export default TodoForms;
